-- ----------------------------
DROP TABLE IF EXISTS `new_e`;
CREATE TABLE `new_e`  (
  `newid` bigint NOT NULL AUTO_INCREMENT COMMENT '文章ID',
  `newcontent` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章内容',
  `newurl` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '路径',
  `newtitle` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标题',
  `newcreatetime` datetime NULL DEFAULT NULL COMMENT '文章创作时间',
  `newsource` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章来源',
  `newauthor` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章作者',
  `collecttime` datetime NULL DEFAULT NULL COMMENT '收集时间',
  `newother` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '其他',
  PRIMARY KEY (`newid`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 23 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '获取文章表' ROW_FORMAT = DYNAMIC;

